INSERT INTO `exemple` (`id`, `titre`, `description`, `header`, `php`, `idRubrique`, `execPHP`, `ordre`, `demo`, `css`) VALUES
(1, 'Single button', NULL, NULL, '$button=new Ajax\\bootstrap\\html\\HtmlButton("btOkay","Okay");\necho $button;', 1, 1, 0, 0, ''),
(2, NULL, NULL, NULL, 'use Ajax\\bootstrap\\html\\HtmlButton;use Ajax\\bootstrap\\html\\base\\CssRef;$bt0=new HtmlButton("bt0","Slamwiki",CssRef::CSS_DANGER,$this->jquery->hide("#bt1"));$bt0->addBadge("Cours");$bt1=new HtmlButton("bt1");$bt1->setValue("Button default");$bt1->addBadge("21");echo $bt0->compile($this->jquery);echo $bt1->compile($this->jquery);echo $this->jquery->compile();', 1, 1, 0, 0, ''),
(3, NULL, NULL, NULL, '$bt=new Ajax\\bootstrap\\html\\HtmlGlyphButton("glButton1","glyphicon-equalizer","Equalizer");\necho $bt;', 6, 1, 0, 0, ''),
(4, NULL, '<p>It is also possible to create a Button glyph from the index of the glyph</p>\n', NULL, '$bt=new Ajax\\bootstrap\\html\\HtmlGlyphButton("glButton2",120,"Glyph numéro 120");\necho $bt;', 6, 1, 2, 0, ''),
(5, NULL, '<p>Overview of all usable glyphicons</p>\n', NULL, '$i=0;\nforeach(Ajax\\bootstrap\\html\\base\\CssRef::glyphIcons() as $glyph){\n	$bt=new Ajax\\bootstrap\\html\\HtmlGlyphButton("glybutton-".$i++,$glyph);\n	$bt->setTitle($glyph);\n	echo $bt;\n}', 6, 1, 3, 0, ''),
(6, 'get', '<p>Performs an Ajax request to the URL <strong>index/main</strong> and shows the HTML response in the area of identifier <strong>response</strong>&nbsp;(the url corresponds to the method <strong>mainAction </strong>of the controller <strong>IndexController</strong>):<br />\n{{warning: The target element <strong>#response</strong> should exist in the page before sending the query.}}</p>\n', NULL, 'public function getAction(){\n	$this->jquery->get("index/main","#response");\n	echo $this->jquery->compile();\n}', 5, 0, 1, 0, ''),
(7, 'Passing parameters', '<p>Same query as the previous but with passing parameters in JSON format : id=1 and page=5&nbsp;</p>\n\n<p>{{info:The parameters <strong>id </strong>and <strong>page </strong>are retrievable in the response by <strong>$_GET["id"]</strong> and <strong>$_GET["page"]</strong>}}</p>\n', NULL, 'public function getAction(){\n	$this->jquery->get("index/main","#response","{id:1,page:5}");\n	echo $this->jquery->compile();\n}', 5, 0, 2, 0, ''),
(8, 'Calling a callback script', '<p>The callBack script passed as a parameter will be executed after a successful ajax request; it can be expressed in php or directly in jQuery.</p>\n', NULL, 'public function getAction(){\n	$this->jquery->get("Test/reponse","#response",null,$this->jquery->show("#response"));\n	echo $this->jquery->compile();\n}\npublic function getAction(){\n	$this->jquery->get("Test/reponse","#response",null,"$(''#response'').show();");\n	echo $this->jquery->compile();\n}', 5, 0, 3, 0, ''),
(9, 'Creation', NULL, NULL, '	$buttonsGroup=$this->jquery->bootstrap()->htmlButtongroups(''bg1'');\n	$buttonsGroup->addElements(array("Bouton1","Bouton2","Bouton3"));\n	echo $buttonsGroup;', 9, 1, 1, 0, ''),
(10, NULL, '<p>Adding an instance of HTML <strong>Button</strong></p>\n', NULL, '$buttonsGroup->addElement(new Ajax\\bootstrap\\html\\HtmlButton("bt4","Bouton4","primary"));\necho $buttonsGroup;', 9, 1, 2, 0, ''),
(11, NULL, '<p>Adding an instance of&nbsp;<strong>HtmlGlyphButton</strong></p>\n', NULL, '	$buttonsGroup->addElement(new Ajax\\bootstrap\\html\\HtmlGlyphButton("bt5","star","Bouton5","warning"));\n	echo $buttonsGroup;', 9, 1, 3, 0, ''),
(12, 'Initialization using an array', '<p>Creation from a more complex array :</p>\n', NULL, '	$buttonsGroup=$this->jquery->bootstrap()->htmlButtongroups(''bg2'');\n	$buttonsGroup->addElements(array(\n				array("value"=>"Bouton1","identifier"=>"bt-1","style"=>"primary"),\n				array("value"=>"Bouton2","identifier"=>"bt-2","style"=>"danger")\n	));\n	$buttonsGroup->getElement(1)->addBadge("Danger");\n	echo $buttonsGroup;', 9, 1, 4, 0, ''),
(13, 'Loading from a model', '<p>Loading from an object list : (<strong>Client</strong> model) :</p>\n\n<p>{{info: The method <strong>fromDatabaseObjects </strong>must take this parameters :</p>\n\n<ul>\n	<li>An array (objects)</li>\n	<li>a function which takes as a parameter an object instance and returns a new element to be inserted into the container HtmlButtonGroups</li>\n</ul>\n\n<p>}}</p>\n', NULL, '	$clients=Client::find();\n	 $buttonsGroup=$this->jquery->bootstrap()->htmlButtongroups(''bg-clients'');\n	echo $buttonsGroup->fromDatabaseObjects($clients, function($client){\n		return new Ajax\\bootstrap\\html\\HtmlButton($client->getId(),$client->getName());\n	});', 9, 1, 4, 0, ''),
(14, 'Event association', NULL, NULL, '	$clients=Client::find();\n	$buttonsGroup=$this->jquery->bootstrap()->htmlButtongroups(''bg-clients2'');\n	$buttonsGroup->fromDatabaseObjects($clients, function($client){\n		return new Ajax\\bootstrap\\html\\HtmlButton("bg-clients2-bt-".$client->getId(),$client->getName());\n	});\n	$buttonsGroup->onClick("$(''#reponse-clients'').html($(event.target).html());");\n	echo $buttonsGroup->compile($this->jquery);\n	echo "<div id=''reponse-clients''>Réponse</div>";', 9, 1, 5, 0, ''),
(15, 'Creation', NULL, NULL, '	$dropdown=$this->jquery->bootstrap()->htmlDropdown("dd1","Default",array("Elément 1","Elément 2"));\n	echo $dropdown->compile();', 11, 1, 1, 0, ''),
(16, 'Adding elements', '<p>Adding an item, addition of several elements, or an item with specific href :</p>\n', NULL, '	$dropdown->addItem("Elément 3");\n	$dropdown->addItems(array("Elément 4","Elément 5"));\n	$dropdown->addItem("Google","http://www.google.com");\n	echo $dropdown;', 11, 1, 2, 0, ''),
(17, 'Headers and dividers', NULL, NULL, '	$dropdown=$this->jquery->bootstrap()->htmlDropdown("dd2","Dropdowns");\n	$dropdown->addHeader("Partie 1");\n	$dropdown->addItems(array("Item1","Item2"));\n	$dropdown->addDivider();\n	$dropdown->addItems(array("Item3","Item4"));\n	echo $dropdown->compile();', 11, 1, 4, 0, ''),
(18, 'Button aspect', NULL, NULL, '	$dropdown->asButton("btn-info");\n	echo $dropdown;', 11, 1, 5, 0, ''),
(19, 'From an array', NULL, NULL, '	$dropdown=$this->jquery->bootstrap()->htmlDropdown("dd2","Dropdown from Array");\n	$dropdown->fromArray(array("items"=>array("Elément 1","Elément 2","-","Elément 3","-Séparateur","Elément 4")));\n	echo $dropdown;', 11, 1, 6, 0, ''),
(20, 'Loading from a model', NULL, NULL, '	$clients=Client::find();\n	$dropdown=$this->jquery->bootstrap()->htmlDropdown(''dd-clients'',''Clients'');\n	echo $dropdown->fromDatabaseObjects($clients, function($client){\n		return $client->getName();\n	});', 11, 1, 7, 0, ''),
(21, 'Event association', NULL, NULL, '	$clients=Client::find();\n	$dropdown=$this->jquery->bootstrap()->htmlDropdown(''dd-clients-2'',''Clients'');\n	$dropdown->fromDatabaseObjects($clients, function($client){\n		return $client->getName();\n	});	\n	$dropdown->onClick("$(''#reponse-clients'').html($(event.target).html());",false,true);\n	echo $dropdown->compile($this->jquery);\n	echo $this->jquery->bootstrap()->htmlAlert("reponse-clients","Aucun client cliqué...","alert-info");', 11, 1, 8, 0, ''),
(22, 'Creation', NULL, NULL, '	$splitBtn=$this->jquery->bootstrap()->htmlSplitbutton("sBtn","Split bouton",array("Item 1","Item 2"));\n	echo $splitBtn;', 12, 1, 1, 0, ''),
(23, 'Event association to the button', NULL, NULL, '	$splitBtn=$this->jquery->bootstrap()->htmlSplitbutton("sBtn-2","Split bouton",array("Item 1","Item 2"));\n	$splitBtn->onButtonClick("$(''#reponse-split'').html(''Clic sur le bouton'');");\n	echo $splitBtn->compile($this->jquery);\n	echo $this->jquery->bootstrap()->htmlAlert("reponse-split","Bouton non cliqué...","alert-info");', 12, 1, 2, 0, ''),
(24, 'Creation', NULL, NULL, '$carousel=$this->jquery->bootstrap()->htmlCarousel("carousel",array("client1.jpg","client2.jpg"));\n$carousel->setBase("public/img/client/");\necho $carousel;', 13, 1, 1, 0, ''),
(25, 'Adding Image', NULL, NULL, '$carousel=$this->jquery->bootstrap()->htmlCarousel("carousel-2",array("client1.jpg","client2.jpg"));\n$carousel->setBase("public/img/client/");\n$carousel->addImage(''client3.jpg'');\necho $carousel;', 13, 1, 2, 0, ''),
(26, 'Loading from a model', '<p>Loading from an object list : (<strong>Client</strong> model) :</p>\n\n<p>{{info: The method <strong>fromDatabaseObjects </strong>must take this parameters :</p>\n\n<ul>\n	<li>An array (objects)</li>\n	<li>a function which takes as a parameter an object instance and returns a new element to be inserted into the container HtmlCarousel</li>\n</ul>\n\n<p>}}</p>\n', NULL, '	$carousel=$this->jquery->bootstrap()->htmlCarousel("carousel-3");\n	$carousel->setBase("public/img/client/");\n	$clients=Client::find();\n	$carousel->fromDatabaseObjects($clients, function($client){\n		return array("client".$client->getId().".jpg","Client".$client->getId(),$client->getName());\n	});\n	echo $carousel->compile($this->jquery);', 13, 1, 4, 0, ''),
(27, 'Creation', '<p>Input with a span (positioned to the left by default)</p>\n', NULL, 'use Ajax\\bootstrap\\html\\HtmlInputgroup;\n		$inputGroup=new HtmlInputgroup("ig-1");\n		$inputGroup->createSpan("@");\n		echo $inputGroup;', 14, 1, 1, 0, ''),
(28, NULL, '<p>Positionned to the wright</p>\n', NULL, 'use Ajax\\bootstrap\\html\\HtmlInputgroup;\n		$inputGroup=new HtmlInputgroup("ig-2");\n		$inputGroup->createSpan("@example.com","right");\n		echo $inputGroup;', 14, 1, 2, 0, ''),
(29, 'Checkboxes and radio buttons', NULL, NULL, 'use Ajax\\bootstrap\\html\\HtmlInputgroup;\n		$inputGroup=new HtmlInputgroup("ig-3");\n		$inputGroup->createCheckbox("ig-3-ck");\n		echo $inputGroup;', 14, 1, 3, 0, ''),
(30, NULL, NULL, NULL, 'use Ajax\\bootstrap\\html\\HtmlInputgroup;\n		$inputGroup=new HtmlInputgroup("ig-4");\n		$inputGroup->createRadio("ig-4-radio","Radio","right");\n		echo $inputGroup;', 14, 1, 4, 0, ''),
(31, 'Button', NULL, NULL, 'use Ajax\\bootstrap\\html\\HtmlInputgroup;\n		$inputGroup=new HtmlInputgroup("ig-5");\n		$inputGroup->createButton("ig-4-radio","Go","right");\n		echo $inputGroup;', 14, 1, 5, 0, ''),
(32, 'SplitButton', NULL, NULL, 'use Ajax\\bootstrap\\html\\HtmlInputgroup;\n		$inputGroup=new HtmlInputgroup("ig-7");\n		$bt=$inputGroup->createSplitButton("ig-7-split","Actions","right",array("Action","Another action","-","Last action"));\n		$bt->setStyle("btn-info");\n		echo $inputGroup;', 14, 1, 7, 0, ''),
(33, 'Buttons group', NULL, NULL, 'use Ajax\\bootstrap\\html\\HtmlInputgroup;\n		$inputGroup=new HtmlInputgroup("ig-5");\n		$inputGroup->createButtons(array("Okay","Annuler"));\n		echo $inputGroup;', 14, 1, 5, 0, ''),
(34, 'Dropdown', NULL, NULL, 'use Ajax\\bootstrap\\html\\HtmlInputgroup;\n		$inputGroup=new HtmlInputgroup("ig-6");\n		$bt=$inputGroup->createDropdown("ig-6-dd","Actions","right",array("Action","Another action","-","Last action"));\n		$bt->setStyle("btn-info");\n		echo $inputGroup;', 14, 1, 6, 0, ''),
(35, NULL, NULL, NULL, 'use Ajax\\bootstrap\\html\\HtmlInputgroup;\nuse Ajax\\bootstrap\\html\\HtmlGlyphButton;\n		$inputGroup=new HtmlInputgroup("ig-5b");\n		$inputGroup->createButtons(array("Okay",new HtmlGlyphButton("glyph",120,"Ouvrir...")));\n		echo $inputGroup;', 14, 1, 5, 0, ''),
(36, 'Events association', '<p> On keystroke in the text field, the class css <strong> btn-success </ strong> is added / removed on the <strong> go </ strong> </p>', NULL, 'use Ajax\\bootstrap\\html\\HtmlInputgroup;\n		$inputGroup=new HtmlInputgroup("ig-8");\n		$inputGroup->createButton("ig-8-button","Go","right");\n		$inputGroup->onKeypress("$(''#ig-8-button'').toggleClass(''btn-success'');");\n		echo $inputGroup->compile($this->jquery);', 14, 1, 8, 0, ''),
(37, 'Resizing', NULL, NULL, '$sizes= Ajax\\bootstrap\\html\\base\\CssRef::sizes("input-group") ;\nfor($i=0;$i<3;$i++){\n	echo "Taille : <b>".$sizes[$i]."</b><br>";\n	$inputGroup->setSize($sizes[$i]);\n	echo $inputGroup;\n}', 14, 1, 8, 0, ''),
(38, 'Creation', NULL, NULL, '$dialog=$this->jquery->bootstrap()->htmlModal("boite1","Titre de la boîte de dialogue","test");\necho $dialog->compile($this->jquery);', 15, 1, 1, 0, ''),
(39, 'Display', '<p>Displays the dialog box on click of a button </p>', NULL, '$button=new Ajax\\bootstrap\\html\\HtmlButton("bt-show","Afficher le dialogue");\n$button->onClick($dialog->jsShow());\necho $button->compile($this->jquery);', 15, 1, 2, 0, ''),
(40, 'Default buttons', '<p>Adding Buttons <strong> Okay </ strong> and <strong> Cancel </ strong> </p>', NULL, '$dialog=$this->jquery->bootstrap()->htmlModal("boite2","Titre de la boîte de dialogue","test");\n$dialog->addOkayButton();\n$dialog->addCancelButton();\necho $dialog->compile($this->jquery);\n$button=new Ajax\\bootstrap\\html\\HtmlButton("bt-show-2","Afficher le dialogue");\n$button->onClick($dialog->jsShow());\necho $button->compile($this->jquery);', 15, 1, 3, 0, ''),
(41, ' exit condition', '<p>To allow the exit of the box by the submit button, it is necessary to set the property<strong> valid <!-- strong--> of the dialog to <strong> true <!-- strong--> </strong></strong></p>\n\n<pre>\n<strong><strong><code class="language-php">$(''#boite5'').prop(''valid'',true);</code></strong></strong></pre>\n', NULL, '$dialog=$this->jquery->bootstrap()->htmlModal("boite5","Titre de la boîte de dialogue","test");\n$dialog->addOkayButton("Enregistrer","$(''#info-2'').html(''clic sur le bouton Valider'');");\n$dialog->addCancelButton("Fermer");\n$btDialog=$this->jquery->bootstrap()->htmlButton("bt-dialog-5","Valide","btn-success","$(''#boite5'').prop(''valid'',true);");\n$btDialog->setToggled();\n$dialog->setContent($btDialog);\necho $dialog->compile($this->jquery);\n$button=new Ajax\\bootstrap\\html\\HtmlButton("bt-show-5","Afficher le dialogue");\n$button->onClick($dialog->jsShow());\necho $button->compile($this->jquery);\necho $this->jquery->bootstrap()->htmlAlert("info-2");', 15, 1, 5, 0, ''),
(42, 'Customize Buttons', NULL, NULL, '$dialog=$this->jquery->bootstrap()->htmlModal("boite4","Titre de la boîte de dialogue","test");\n$dialog->addOkayButton("Enregistrer","$(''#info'').html(''clic sur le bouton Valider'');");\n$dialog->addCancelButton("Fermer");\necho $dialog->compile($this->jquery);\n$button=new Ajax\\bootstrap\\html\\HtmlButton("bt-show-4","Afficher le dialogue");\n$button->onClick($dialog->jsShow());\necho $button->compile($this->jquery);\necho $this->jquery->bootstrap()->htmlAlert("info");', 15, 1, 4, 0, ''),
