(44, 'Calling an action on a controller', '<p>Controller <b>test</b> :<br />&nbsp;</p><pre><code class="language-php">	public function clientsAction(){		$clients=Client::find();		$this-&gt;view-&gt;setRenderLevel(View::LEVEL_MAIN_LAYOUT);		$this-&gt;view-&gt;setVars(array(" model=""&gt;"Clients","objects"=&gt;$clients));	}</code></pre>', NULL, '$dialog=$this->jquery->bootstrap()->htmlModal("boite7","Liste des clients");\n$dialog->addOkayButton();\n$dialog->addCancelButton();\n$dialog->forward($this,"test","clients");\necho $dialog->compile($this->jquery);\n$button=new Ajax\\bootstrap\\html\\HtmlButton("bt-show-7","Afficher le dialogue");\n$button->onClick($dialog->jsShow());\necho $button->compile($this->jquery);', 15, 1, 7, 0, ''),
