(121, 'attr', '<p>The .attr() method sets the value of attributes</p>\n\n<p><input class="form-control" id="input" /></p>\n', NULL, '$this->jquery->attr("#input","value","Essai",true);', 29, 1, 4, 0, ''),
(122, 'json', '<div class="ui segment" id="ex1">\n<p><label class="ui label" id="id">id</label></p>\n\n<p><label class="ui label" id="name">name</label></p>\n</div>\n', NULL, '$this->jquery->json("json/json/1","get","{}",null,"id","$(''#ex1'')",true);', 30, 1, 1, 0, ''),
(123, 'jsonOn with a callback', '<p>The <strong>callback </strong>function is used here to change the semantic progressbar.</p>\n\n<p><a class="ui button btAjax" data-ajax="1">Client 1</a> <a class="ui button btAjax" data-ajax="2">Client 2</a> <a class="ui button btAjax" data-ajax="3">Client 3</a></p>\n\n<div class="ui warning message" id="ex3">\n<div class="ui label" id="name">&nbsp;</div>\n&nbsp;\n\n<div class="ui indicating progress" id="pb2">\n<div class="bar">\n<div class="progress">&nbsp;</div>\n</div>\n</div>\n</div>\n', NULL, '$this->jquery->jsonOn("click",".btAjax","json/json",array("attr"=>"data-ajax","context"=>"$(''#ex3'')","jsCallback"=>"$(''#pb2'').progress({''percent'': data[''part'']});"));', 30, 1, 3, 0, ''),
(124, 'jsonArray', '<p><a class="ui teal button" id="btJsonArray">Charger les clients</a></p>\n\n<div class="ui link cards" id="ex3">\n<div class="ui card hide" id="repeat">\n<div class="image"><img src="public/img/client/client[[id]].jpg" /></div>\n\n<div class="content"><a class="header">[[name]]</a>\n\n<div class="meta"><span>Part : [[part]]%</span></div>\n</div>\n\n<div class="ui bottom attached progress" data-percent="[[part]]">\n<div class="bar" style="width:[[part]]%">&nbsp;</div>\n</div>\n</div>\n</div>\n', NULL, '$this->jquery->jsonArrayOn("click","#btJsonArray","#repeat", "json/jsonArray", array());', 30, 1, 4, 0, ''),
(125, 'jsonOn', '<p><a class="ui button btAjax2" data-ajax="1">Client 1</a> <a class="ui button btAjax2" data-ajax="2">Client 2</a></p>\n\n<div class="ui positive message" id="ex2">\n<div class="ui disabled input"><input id="name" type="text" value="" /></div>\n</div>\n', NULL, '$this->jquery->jsonOn("click",".btAjax2","json/json",array("attr"=>"data-ajax","context"=>"$(''#ex2'')"));', 30, 1, 2, 0, ''),
(126, 'Creation', NULL, NULL, 'use Ajax\\bootstrap\\html\\HtmlPanel;\n$gs=$this->jquery->bootstrap()->htmlGridSystem("gs-2");\n$gs->getCell(1,1)->setContent(new HtmlPanel("p1","1,1"));\n$gs->getCell(2,2)->setContent(new HtmlPanel("p2","2,2"))->setWidthForAll(6);\n$gs->getCell(1,3)->setContent(new HtmlPanel("p3","1,3"))->setWidthForAll(2);\n\necho $gs;', 31, 1, 2, 0, ''),
(127, 'Creation', NULL, NULL, 'use Ajax\\bootstrap\\html\\HtmlPanel;\n$gs=$this->jquery->bootstrap()->htmlGridSystem("gs-1",6,12);\n$gs->setContentForAll("gs");\n$gs->getCell(3,6)->setContent("milieu");\n$gs->getRow(3)->merge("md",6,5);\n$gs->getRow(5)->merge("md",1,11);\n\necho $gs;', 31, 1, 1, 0, ''),
(128, 'Adding a label to an element', NULL, NULL, 'use Ajax\\bootstrap\\html\\base\\HtmlBsDoubleElement;$p=new HtmlBsDoubleElement("p");$p->setContent("this is a paragraph with a label");$p->addLabel("new");echo $p;', 32, 1, 0, 0, ''),
(129, 'Création', '<div class="ui labeled button" tabindex="0">\n  <div class="ui red button">\n    <i class="heart icon"></i> Like\n  </div>\n  <a class="ui basic red left pointing label">\n    1,048\n  </a>\n</div>\n<div class="ui labeled button" tabindex="0">\n  <div class="ui basic blue button">\n    <i class="fork icon"></i> Forks\n  </div>\n  <a class="ui basic left pointing blue label">\n    1,048\n  </a>\n</div>', NULL, '$mess=new \\Ajax\\semantic\\html\\collections\\HtmlMessage("mess1","<p>We just updated our privacy policy here to better service our customers. We recommend reviewing the changes.</p>");\n		$mess->addHeader("Changes in Service");\n		$mess->addList(array("You can now have cover images on blog pages","Drafts will now auto-save while writing"),false);\necho $mess;', 38, 1, 1, 1, ''),
(130, 'Icône', NULL, 'Header', '$mess=new Ajax\\semantic\\html\\collections\\HtmlMessage("mess2","<p>We just updated our privacy policy here to better service our customers. We recommend reviewing the changes.</p>");\n		$mess->addHeader("Changes in Service");\n		$mess->setIcon("inbox");\necho $mess;', 38, 1, 2, 0, ''),
(131, 'Creation', 'A standard breadcrumb', NULL, '$bc=$semantic->htmlBreadcrumb("bc1",array("Home","Store","T-shirt"));\necho $bc;', 34, 1, 1, 1, ''),
(132, 'Divider', '<p>A breadcrumb can contain a divider to show the relationship between sections, this can be formatted as an icon or text.</p>\n', NULL, 'use Ajax\\semantic\\html\\collections\\HtmlBreadcrumb;\n$bc=new HtmlBreadcrumb("bc2",array("Home","Store","T-shirt"));\n$bc->setContentDivider(">");\necho $bc;', 36, 1, 2, 0, ''),
(133, 'Icon divider', NULL, NULL, 'use Ajax\\semantic\\html\\collections\\HtmlBreadcrumb;\n\n$bc=new HtmlBreadcrumb("bc3",array("Home","Store","T-shirt"));\n$bc->setIconContentDivider("right angle");\necho $bc;', 36, 1, 3, 0, ''),
(135, 'Various dividers', NULL, NULL, 'use Ajax\\semantic\\html\\collections\\HtmlBreadcrumb;\n\n$bc=new HtmlBreadcrumb("bc3",array("Home","Store","T-shirt"));\n$bc->setIconContentDivider("right caret",1);\necho $bc;', 36, 1, 4, 1, ''),
(136, 'Directly in the controller (without view)', NULL, NULL, '$semantic=$this->jquery->semantic();\n$button=$semantic->htmlButton("btAfficher","Afficher message")->setColor("red");\n$message=$semantic->htmlMessage("message1","<b>Cliquer</b> sur le bouton...");\n$button->onClick($message->jsHtml("Click sur bouton"));\necho $button->compile($this->jquery);\necho $message;', 37, 1, 1, 0, ''),
(138, 'Text', '<p>A breadcrumb can contain sections that can either be formatted as a link or text</p>\n', NULL, '$bc=$this->jquery->semantic()->htmlBreadcrumb("bc4",array("Home","Store","T-shirt"));\n$bc->asTexts();\necho $bc;\n', 36, 1, 4, 0, ''),
(139, 'Individuals elements', '<p>A section may be linkable or contain a link</p>\n', NULL, '$bc=$this->jquery->semantic()->htmlBreadcrumb("bc5",array("Home")); \n$bc->addItem("Search for: <a href=''#''>paper towels</a>");\necho $bc;\n', 36, 1, 5, 0, ''),
(140, 'Creation', '<p>A standard button</p>\n', NULL, '$bt=$this->jquery->semantic()->htmlButton("bt1","Follow");\necho $bt;', 39, 1, 1, 0, ''),
(141, 'jQuery', '<p>A button can interract with jQuery</p>\n', NULL, '$bt=$semantic->htmlButton("bt2","Follow");\n$bt->onClick("$(this).toggleText(''Following'',''Follow'')");\necho $bt->compile($this->jquery);', 39, 1, 2, 0, ''),
(142, 'Types', '<p>{{warning:Although any tag can be used for a button, it will only be keyboard focusable if you use a&lt;button&gt;tag or you add the property tabindex="0". Keyboard accessible buttons will preserve focus styles after click, which may be visually jarring.}}</p>\n', NULL, '$bt3=$semantic->htmlButton("bt3","Button");\n$bt4=$semantic->htmlButton("bt4","Focusable")->setFocusable();\necho $bt3.$bt4;', 39, 1, 3, 0, ''),
(143, 'Emphasis', '<p>A button can be formatted to show different levels of emphasis<br />\n{{info:Setting your brand colors to primary and secondary color variables in site.variables will allow you to use your color theming for UI elements}}</p>\n', NULL, 'use Ajax\\semantic\\html\\base\\constants\\Emphasis;\n$bt5=$semantic->htmlButton("bt5","Save")->setEmphasis(Emphasis::PRIMARY);\n$bt6=$semantic->htmlButton("bt6","Discard");\necho $bt5.$bt6;', 39, 1, 3, 0, ''),
(144, 'Animated', '<p>A button can animate to show hidden content {{info:The button will be automatically sized according to the visible content size. Make sure there is enough room for the hidden content to show}}</p>\n', NULL, 'use Ajax\\semantic\\html\\elements\\HtmlIcon;\n$bt1=$semantic->htmlButton("bt7","Sign-up for a Pro account");\n$bt1->setAnimated("$12.99 a month");\n$bt2=$semantic->htmlButton("bt8","Next");\n$bt2->setAnimated(new HtmlIcon("","right arrow"),"fade right");\necho $bt1.$bt2;\n', 39, 1, 5, 0, ''),
(145, 'Labeled', '<p>A button can appear alongside a label</p>\n', NULL, '$bt=$semantic->htmlButton("bt9"," Like");\n$bt->addIcon("heart");\n$bt->addLabel("2,048")->setPointing("left");\necho $bt;', 39, 1, 6, 1, ''),
(146, 'Labeled icon', '<p>A button can use an icon as a label</p>\n', NULL, '$bt=$semantic->htmlButton("bt11","Pause");\n$bt->addIcon("pause",false,true);\necho $bt;', 39, 1, 8, 0, ''),
(147, 'Icon', '<p>A button can have only an icon</p>\n', NULL, '$bt=$semantic->htmlButton("bt10")->asIcon("pause");\necho $bt;\n', 39, 1, 7, 0, ''),
(148, 'Basic', '<p>A basic button is less pronounced</p>\n', NULL, '$bt=$semantic->htmlButton("bt11","Basic");\n$bt->setBasic();\necho $bt;', 39, 1, 9, 0, ''),
(149, 'Inverted', '<p>A button can be formatted to appear on dark backgrounds</p>\n', NULL, '$bt=$semantic->htmlButton("","Inverted");\n$bt->setInverted();\n\necho $semantic->htmlSegment("",$bt)->setInverted();', 39, 1, 10, 0, ''),
(150, 'Colors', NULL, NULL, 'use Ajax\\semantic\\html\\base\\constants\\Color;\n$bts="";\nforeach(Color::getConstants() as $k=>$v){\n$bts.=$semantic->htmlButton("",$k)->setColor($v);\n}\necho $bts;', 42, 1, 2, 0, ''),
(151, 'Creation', NULL, NULL, '$buttons=$semantic->htmlButtonGroups("bg1",array("One","Two","Three"));\necho $buttons;', 40, 1, 1, 0, ''),
(153, 'Icon Buttons', '<p>Button groups can show groups of icons</p>\n', NULL, '$buttons=$semantic->htmlButtonGroups("bg2",array("align left","align center","align right","align justify"));\n$buttons->asIcons();\necho $buttons;', 40, 1, 2, 0, ''),
(154, 'Conditionnal', '<p>Button groups can contain conditionals</p>\n', NULL, '$buttons=$semantic->htmlButtonGroups("bg3",array("Cancel","Update"));\n$buttons->getElement(1)->setPositive();\n$buttons->insertOr(0);\necho $buttons;\n', 40, 1, 3, 1, ''),
(155, 'Disabled', '<p>A button can show it is currently unable to be interacted with</p>\n', NULL, '$bt=$semantic->htmlButton("","Follow");\n$bt->setDisabled();\necho $bt;', 41, 1, 1, 0, ''),
(156, 'Loading', '<p>A button can show a loading indicator</p>\n', NULL, '$bt=$semantic->htmlButton("","Follow");\n$bt->setLoading();\necho $bt;', 41, 1, 2, 0, ''),
(157, 'Social', '<p>A button can be formatted to link to a social website</p>\n', NULL, 'use Ajax\\semantic\\html\\elements\\HtmlButton;\nuse Ajax\\semantic\\html\\base\\constants\\Social;\n$bt=HtmlButton::social("",Social::FACEBOOK);\necho $bt;', 42, 1, 3, 0, ''),
(158, 'Accordion', '<p>A standard accordion</p>\n', NULL, '$acc=$semantic->htmlAccordion("accordion1");\n$acc->addItem(array("What is a dog?","A dog is a type of domesticated animal. Known for its loyalty and faithfulness, \nit can be found as a welcome guest in many households across the world."));\n$acc->addItem(array("What kinds of dogs are there?","There are many breeds of dogs. Each breed varies in size and temperament.\n Owners often select a breed of dog that they find to be compatible with their own lifestyle and desires from a companion."));\necho $acc->compile($this->jquery);', 43, 1, 1, 0, ''),
(159, 'Styled', NULL, NULL, '$acc=$semantic->htmlAccordion("accordion2");\n$acc->addItem(array("What is a dog?","A dog is a type of domesticated animal. Known for its loyalty and faithfulness, \nit can be found as a welcome guest in many households across the world."));\n$acc->addItem(array("What kinds of dogs are there?","There are many breeds of dogs. Each breed varies in size and temperament.\n Owners often select a breed of dog that they find to be compatible with their own lifestyle and desires from a companion."));\n$acc->setStyled();\necho $acc->compile($this->jquery);', 43, 1, 2, 1, ''),
(160, 'Fluid', NULL, NULL, '$acc=$semantic->htmlAccordion("accordion3");\n$acc->addItem(array("What is a dog?","A dog is a type of domesticated animal. Known for its loyalty and faithfulness, \nit can be found as a welcome guest in many households across the world."));\n$acc->addItem(array("What kinds of dogs are there?","There are many breeds of dogs. Each breed varies in size and temperament.\n Owners often select a breed of dog that they find to be compatible with their own lifestyle and desires from a companion."));\n$acc->setStyled();\n$acc->setFluid();\necho $acc->compile($this->jquery);', 44, 1, 1, 0, ''),
(161, 'Inverted', NULL, NULL, '$acc=$semantic->htmlAccordion("accordion4");\n$acc->addItem(array("What is a dog?","A dog is a type of domesticated animal. Known for its loyalty and faithfulness, \nit can be found as a welcome guest in many households across the world."));\n$acc->addItem(array("What kinds of dogs are there?","There are many breeds of dogs. Each breed varies in size and temperament.\n Owners often select a breed of dog that they find to be compatible with their own lifestyle and desires from a companion."));\n$acc->setInverted();\n\n$segment=$semantic->htmlSegment("seg1",$acc);\n$segment->setInverted();\necho $segment->compile($this->jquery);', 44, 1, 2, 0, '');
INSERT INTO `exemple` (`id`, `titre`, `description`, `header`, `php`, `idRubrique`, `execPHP`, `ordre`, `demo`, `css`) VALUES
(162, 'Accordion menu', '<p>An accordion can be used to create content drawers inside a menu</p>\n', NULL, '$am=$semantic->htmlAccordionMenu("am");\n$am->addItem(array("Element 1","Element 1 content"));\n$am->addItem(array("Element 2",$semantic->htmlMessage("","Element 2 content")));\n$am->getItem(0)->setActive();\necho $am->compile($this->jquery);', 44, 1, 5, 0, ''),
(163, 'Form', '<p>a form</p>\n', NULL, '$form=$semantic->htmlForm("frm1");\n$form->addInput("firstname","First Name");\n$form->addInput("lastname","Last Name");\n$form->addCheckbox("ckAgree","I agree to the Terms and Conditions",NULL,"toggle");\n$form->addButton("","Submit")->asSubmit();\necho $form;', 45, 1, 1, 1, ''),
(164, 'Complete form', NULL, NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormInput;\nuse Ajax\\semantic\\html\\collections\\form\\HtmlFormDropdown;\n$form=$semantic->htmlForm("frm2");\n$form->addHeader("Shipping Information",3);\n$form->addFields(new HtmlFormInput("lastname"),new HtmlFormInput("firstname"),"Name");\n$form->addFields(new HtmlFormInput("shipping[address]"),new HtmlFormInput("shipping[address-2]"),"Billing Address");\n$form->getField(0)->setPlaceholder("Last Name...");\n$form->getField(1)->setPlaceholder("First Name...");\n$form->getField(2)->setWidth(12)->setPlaceholder("Address...");\n$form->getField(3)->setWidth(4)->setPlaceholder("Apt #");\n$form->addItem(new HtmlFormDropdown("ddCountry",array("France","Germany","Norway"),"Country"));\n$form->addHeader("Receipt",3);\n$dd=$form->addItem(HtmlFormDropdown::multipleDropdown("ddTo",array(),"Send Receipt To:"));\n$dd->addItem("Jenny Hess","jenny","http://semantic-ui.com/images/avatar/small/jenny.jpg");\n$dd->addItem("Stevie Feliciano","stevie","http://semantic-ui.com/images/avatar/small/stevie.jpg");\n$dd->addItem("Christian","christian","http://semantic-ui.com/images/avatar/small/christian.jpg");\necho $form;', 46, 1, 15, 0, ''),
(165, 'Field', '<p>A field is a form element containing a label and an input</p>\n', NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormInput;\n$form=$semantic->htmlForm("frm3");\n$form->addItem(new HtmlFormInput("ui","User input"));\necho $form;', 46, 1, 1, 0, ''),
(166, 'Fields', '<p>A set of fields can appear grouped together</p>\n', NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormInput;\n$form=$semantic->htmlForm("frm4");\n$form->addFields(new HtmlFormInput("fName","First Name"),new HtmlFormInput("mName","Middle Name"),new HtmlFormInput("lName","Last Name"));\n$fields=$form->addFields();\n$fields->addInput("fName","First Name")->setWidth(10);\n$fields->addInput("mName","Middle Name")->setWidth(3);\n$fields->addInput("lName","Last Name")->setWidth(3);\necho $form;', 46, 1, 2, 0, ''),
(167, 'TextArea', '<p>A textarea can be used to allow for extended user input.</p>\n', NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormTextarea;\n$form=$semantic->htmlForm("frm5");\n$form->addItem(new HtmlFormTextarea("text1","Text"));\n$form->addItem(new HtmlFormTextarea("text2","Short Text"))->setRows(2);\necho $form;', 46, 1, 3, 0, ''),
(168, 'Checkbox', '<p>A form can contain a checkbox</p>\n', NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormCheckbox;\nuse Ajax\\semantic\\html\\base\\constants\\CheckboxType;\n$form=$semantic->htmlForm("frm6");\n$form->addItem(new HtmlFormCheckbox("ck1","Checkbox"));\n$form->addItem(new HtmlFormCheckbox("ck2","Toggle"))->setType(CheckboxType::TOGGLE);\n$form->addItem(new HtmlFormCheckbox("ck3","Slider"))->setType(CheckboxType::SLIDER);\necho $form;\n', 46, 1, 4, 0, ''),
(169, 'Radio', NULL, NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormFields;\n$form=$semantic->htmlForm("frm7");\n$form->addField(HtmlFormFields::radios("fruit",array("Apples","Oranges","Pears","Grapefruit"),"Select your favorite fruit : "))->setInline();\necho $form;', 46, 1, 5, 0, ''),
(170, 'Grouped fields', NULL, NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormFields;\n$form=$semantic->htmlForm("frm8");\n$form->addField(HtmlFormFields::radios("fruit-2",array("Apples","Oranges","Pears","Grapefruit"),"Select your second favorite fruit : "))->setGrouped();\necho $form;', 46, 1, 6, 0, ''),
(171, 'Dropdown', NULL, NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormDropdown;\n$form=$semantic->htmlForm("frm9");\n$form->addField(new HtmlFormDropdown("genre",array("Male","Female"),"Genre"));\necho $form;', 46, 1, 9, 0, ''),
(172, 'Multiple select', '<p>A multiple select is used to include several choices with one form field</p>\n', NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormDropdown;\n$form=$semantic->htmlForm("frm10");\n$form->addDropdown("country",array("Angola","France","Germany"),"Country","",true);\necho $form;', 46, 1, 10, 0, ''),
(173, 'Message', '<p>A form can contain a message</p>\n', NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormInput;\nuse Ajax\\semantic\\html\\elements\\HtmlList;\n$form=$semantic->htmlForm("frm11");\n$form->addMessage("",new HtmlList("",array("Please enter your first name","Please enter your last name")),"We had some issues","user");\n//$form->addFields(new HtmlFormInput("fName","First Name"),new HtmlFormInput("lName","Last Name"));\n$form->addInputs(array(["identifier"=>"firstName","label"=>"firstName","placeholder"=>"Enter your firstname..."]),"Name");\necho $form;', 46, 1, 11, 0, ''),
(174, 'Loading', '<p>If a form is in loading state, it will automatically show a loading indicator.</p>\n', NULL, '$form=$semantic->htmlForm("form12");\n$form->addInput("email","E-mail");\n$form->addButton("btSubmit","Submit");\n$form->setLoading();\necho $form;', 47, 1, 1, 0, ''),
(175, 'Error', '<p>If a form is in an error state, it will automatically show any error message blocks.</p>\n', NULL, 'use Ajax\\semantic\\html\\base\\constants\\State;\n$form=$semantic->htmlForm("form13");\n$form->addInput("email","E-mail");\n$form->addMessage("msg","That e-mail has been subscribed, but you have not yet clicked the verification link in your e-mail.","Could you check something!",NULL,State::ERROR);\n$form->addButton("btSubmit","Submit");\n$form->setState(State::ERROR);\necho $form;', 47, 1, 2, 0, ''),
(176, 'jQuery', '<p>The state of the form can be modified with jQuery<br />\nIf a form is in an success state, it will automatically show any success message blocks.</p>\n', NULL, 'use Ajax\\semantic\\html\\base\\constants\\State;\n$form=$semantic->htmlForm("form14");\n$form->addInput("email","E-mail");\n$form->addMessage("msg","You''re all signed up for the newsletter.","Form Completed",NULL,State::SUCCESS);\n$form->addButton("btSubmit2","Submit",NULL,$form->jsState(State::SUCCESS));\necho $form->compile($this->jquery);', 47, 1, 4, 0, ''),
(177, 'Error fields', '<p>Individual fields may display an error state</p>\n', NULL, 'use Ajax\\semantic\\html\\base\\constants\\State;\n$form=$semantic->htmlForm("form15");\n$email=$form->addInput("email15","E-mail");\n$ck=$form->addCheckbox("ck-email","I agree to the Terms and Conditions");\n$form->addButton("btSubmit-error","Submit",NULL,$email->jsState(State::ERROR).$ck->jsState(State::ERROR));\necho $form->compile($this->jquery);', 47, 1, 5, 0, ''),
(178, 'Disable field', '<p>Individual fields may be disabled</p>\n', NULL, '$form=$semantic->htmlForm("frm16");\n$form->addInput("firstName","First Name")->setDisabled();\necho $form;', 47, 1, 5, 0, ''),
(179, 'Read-only field', '<p>Individual fields may be read only</p>\n', NULL, '$form=$semantic->htmlForm("form17");\n$form->addInput("firstName","First Name")->setReadonly();\necho $form;', 47, 1, 6, 0, ''),
(180, 'Size', '<p>A form can also be small or large</p>\n', NULL, 'use Ajax\\semantic\\html\\base\\constants\\Size;\n$form=$semantic->htmlForm("form17");\n$form->addInput("firstName","First Name");\n$form->addInput("lastName","Last Name");\necho $form;\n$form->setSize(Size::LARGE);\necho $form;', 48, 1, 1, 0, ''),
(181, 'Menu', '<p>A menu</p>\n', NULL, '$menu=$semantic->htmlMenu("menu0",array("Editorials","reviews","Upcoming Events"))->setWidth(3);\necho $menu;', 49, 1, 1, 0, ''),
(182, 'Icon menu', NULL, NULL, '$menu=$semantic->htmlIconMenu("menu1",["star","user","video"]);\necho $menu;', 49, 1, 2, 0, ''),
(183, 'Labeled icon menu', NULL, NULL, '$menu=$semantic->htmlLabeledIconMenu("menu2",array(["star","Star"],["user","User"],["video","Video"]));\necho $menu;', 49, 1, 3, 0, ''),
(184, 'Classic Menu', NULL, NULL, '$menu=$semantic->htmlMenu("menu3");\n$menu->addImage('''',"http://semantic-ui.com/images/logo.png");\n$menu->additems(["Features","Testimonials","Sign in"]);\necho $menu;', 49, 1, 4, 0, ''),
(185, 'Dropdown', NULL, NULL, '$menu=$semantic->htmlMenu("menu4");\n$menu->addImage('''',"http://semantic-ui.com/images/logo.png");\n$menu->addDropdownAsItem("Browse",["Features","Testimonials","Sign in"]);\necho $menu;', 49, 1, 4, 1, ''),
(186, 'Popup', NULL, NULL, '$menu=$semantic->htmlMenu("menu5");\n$popup=$menu->addPopupAsItem("item","pop");\n$popup->addList(["Cashmere","Linen","Cotton","Viscose"],"Fabrics");\n$popup->addList(["Small","Medium","Large","Plus sizes"],"Size");\n$popup->addList(["Food Studies","Journalism","Non Profit Management"],"Social Sciences");\n$menu->addItem("Last");\necho $menu->compile($this->jquery);', 49, 1, 5, 0, ''),
(187, 'Grid', '<p>A basic grid</p>\n', NULL, '$grid=$semantic->htmlGrid("grid1");\n$grid->setColsCount(16);\necho $grid;', 50, 1, 1, 0, ''),
(188, 'Divided grid', '<p>A grid can have dividers between its columns</p>\n', NULL, '$grid=$semantic->htmlGrid("grid2",2,3);\n$grid->setDivided();\necho $grid;', 50, 1, 2, 0, ''),
(189, 'Vertically Divided', '<p>A grid can have dividers between rows</p>\n', NULL, '$grid=$semantic->htmlGrid("grid3",2,3);\n$grid->setDivided(true);\necho $grid;', 50, 1, 3, 0, ''),
(190, 'Celled grid', '<p>A grid can have rows divided into cells</p>\n', NULL, '$grid=$semantic->htmlGrid("grid4",2,3);\n$grid->setCelled();\necho $grid;', 50, 1, 4, 0, ''),
(191, 'Internally celled', '<p>A grid can have rows divisions only between internal rows</p>\n', NULL, '$grid=$semantic->htmlGrid("grid5",2,3);\n$grid->setCelled(true);\necho $grid;', 50, 1, 5, 0, ''),
(192, 'Floated', '<p>A column can sit flush against the left or right edge of a row</p>\n', NULL, '$grid=$semantic->htmlGrid("grid6",1,2);\n$grid->getItem(0)->setWidth(5)->setFloated("left");\n$grid->getItem(1)->setWidth(5)->setFloated("right");\necho $grid;', 51, 1, 6, 0, ''),
(193, 'Column Width', '<p>A column can vary in width taking up more than a single grid column.</p>\n', NULL, '$grid=$semantic->htmlGrid("grid7",1,3);\n$grid->getItem(0)->setWidth(4);\n$grid->getItem(1)->setWidth(9);\n$grid->getItem(2)->setWidth(3);\necho $grid;', 51, 1, 7, 0, ''),
(194, 'Column Count', '<p>A grid can have a different number of columns per row</p>\n', NULL, '$grid=$semantic->htmlGrid("grid8",3);\n$grid->getRow(0)->setColsCount(3);\n$grid->getRow(1)->setColsCount(4);\n$grid->getRow(2)->setColsCount(5);\necho $grid;', 51, 1, 8, 0, ''),
(195, 'Stretched', '<p>A grid or a row can stretch its contents to take up the entire column height</p>\n', NULL, 'use Ajax\\semantic\\html\\elements\\HtmlSegment;\n$grid=$semantic->htmlGrid("Grid9",1,3)->setStretched();\n$grid->setValues([new HtmlSegment("","1"),array(new HtmlSegment("","1"),new HtmlSegment("","2")),array(new HtmlSegment("","1"),new HtmlSegment("","2"),new HtmlSegment("","3"))]);\necho $grid;', 51, 1, 9, 0, ''),
(196, 'Relaxed', '<p>A grid can increase its gutters to allow for more negative space</p>\n', NULL, '$grid=$semantic->htmlGrid("grid10",2,4)->setRelaxed();\necho $grid; ', 51, 1, 10, 0, ''),
(197, 'Colored', '<p>A grid row or column can be colored</p>\n', NULL, 'use Ajax\\semantic\\html\\base\\constants\\Color;\n$grid=$semantic->htmlGrid("Grid11");\n$colors=Color::getConstants();\nforeach($colors as $color){\n    $grid->addCol()->setValue($color)->setColor($color);\n}\necho $grid->setWide(5);', 51, 1, 11, 0, ''),
(198, 'Colored rows', NULL, NULL, 'use Ajax\\semantic\\html\\base\\constants\\Color;\n$grid=$semantic->htmlGrid("Grid12");\n$colors=Color::getConstants();\nforeach($colors as $color){\n    $grid->addRow(1)->setValues([$color])->setColor($color);\n}\necho $grid;', 51, 1, 12, 0, ''),
(199, 'Centered', '<p>A grid can have its columns centered</p>\n', NULL, '$grid=$semantic->htmlGrid("grid13",3);\n$grid->getRow(0)->addCols(2);\n$grid->getRow(1)->addCols(3);\n$grid->getRow(2)->addCols(4);\necho $grid->setCentered();', 51, 1, 13, 0, ''),
(200, 'Text Alignment', '<p>A grid, row, or column can specify its text alignment</p>\n', NULL, 'use Ajax\\semantic\\html\\collections\\menus\\HtmlMenu;\nuse Ajax\\semantic\\html\\base\\constants\\TextAlignment;\n$grid=$semantic->htmlGrid("Grid14",1,3);\n$grid->setValues([HtmlMenu::vertical("",["Cats"]),HtmlMenu::vertical("",["Dogs","Poodle","Cockerspaniel"]),HtmlMenu::vertical("",["Monkeys"])]);\n$grid->setTextAlignment(TextAlignment::CENTER);\necho $grid;', 51, 1, 14, 0, ''),
(201, 'Vertical alignment', '<p>A grid, row, or column can specify its vertical alignment to have all its columns vertically centered.</p>\n', NULL, 'use Ajax\\semantic\\html\\elements\\HtmlSegment;\nuse Ajax\\semantic\\html\\base\\constants\\VerticalAlignment;\n$grid=$semantic->htmlGrid("Grid15",1,3)->setVerticalAlignment(VerticalAlignment::MIDDLE);\n$grid->setValues([new HtmlSegment("","1"),array(new HtmlSegment("","1"),new HtmlSegment("","2")),array(new HtmlSegment("","1"),new HtmlSegment("","2"),new HtmlSegment("","3"))]);\necho $grid;', 51, 1, 15, 0, ''),
(202, 'Column Widths', '<p>Column widths can be specified using (x) wide class names.&nbsp;If a column cannot fit in a row it will automatically flow to the next row</p>\n', NULL, '$grid=$semantic->htmlGrid("grid16");\n$grid->addCols([8,8,10,6,4,12,2,14,16]);\necho $grid;', 51, 1, 16, 0, ''),
(203, 'Grouping rows', '<p>Row wrappers allow you to apply variations to a group of columns.</p>\n', NULL, '$grid=$semantic->htmlGrid("grid17")->setWidth(4);\n$grid->addRow()->setWidth(2)->addCol();\n$grid->addRow(4);\necho $grid;', 51, 1, 17, 0, ''),
(204, 'Checkbox', '<p>A standard checkbox</p>\n', NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormCheckbox;\n$ck=new HtmlFormCheckbox("c1","Make my profile visible");\necho $ck;', 52, 1, 1, 0, ''),
(205, 'Radio', '<p>A checkbox can be formatted as a radio element. This means it is an exclusive option.</p>\n', NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormRadio;\n$radio=new HtmlFormRadio("r2","r2","Radio choice");\necho $radio;', 52, 1, 2, 0, ''),
(206, 'Radios', NULL, NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormFields;\n$radios=HtmlFormFields::radios("radios1",["Once a week","2-3 times a week","Once a day","Twice a day"],"How often do you use checkboxes?");\necho $radios;', 52, 1, 4, 0, ''),
(207, 'Inline radios', NULL, NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormFields;\n$form=$semantic->htmlForm("frm5");\n$radios=HtmlFormFields::radios("radios2",["Once a week","2-3 times a week","Once a day","Twice a day"],"How often do you use checkboxes?");\n$form->addField($radios)->setInline();\necho $form;', 52, 1, 5, 0, ''),
(208, 'Slider', '<p>A checkbox can be formatted to emphasize the current selection state</p>\n', NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormCheckbox;\nuse Ajax\\semantic\\html\\base\\constants\\CheckboxType;\n$ck=new HtmlFormCheckbox("slider1","Make my profile visible");\n$ck->setType(CheckboxType::SLIDER);\necho $ck;', 52, 1, 6, 0, ''),
(209, 'Radio sliders', NULL, NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormFields;\nuse Ajax\\semantic\\html\\base\\constants\\CheckboxType;\n$form=$semantic->htmlForm("frm7");\n$radios=HtmlFormFields::radios("radios7",["20 mbps max","10 mbps max","5 mbps max","Unmetered"],"Outbound Throughput",0,CheckboxType::SLIDER);\n$form->addField($radios)->setGrouped();\necho $form;', 52, 1, 7, 1, ''),
(210, 'Toggle', '<p>A checkbox can be formatted to show an on or off choice</p>\n', NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormCheckbox;\nuse Ajax\\semantic\\html\\base\\constants\\CheckboxType; \n$ck=new HtmlFormCheckbox("toogle1","Subscribe to weekly newsletter");\n$ck->setType(CheckboxType::TOGGLE);\necho $ck;', 52, 1, 7, 0, ''),
(211, 'Read-only', '<p>A checkbox can be read-only and unable to change states</p>\n', NULL, '$ck=$semantic->htmlCheckbox("readonly","Read only");\n$ck->setReadonly();\necho $ck;', 53, 1, 1, 0, ''),
(212, 'Grouped checkboxes', NULL, NULL, 'use Ajax\\semantic\\html\\elements\\HtmlList;\n$list=new HtmlList("lst-checked");\n$list->addCheckedList(["Apple","Orange","Pear"],"Fruits");\n$list->addCheckedList(["Lettuce","Carrot","Spinach"],"Vegetables",[0,1]);\necho $list->compile($this->jquery);', 52, 1, 10, 0, ''),
(213, 'Attaching Events to other Elements', '<p>Checkbox can use the attach events behavior to attach events easily to other activating elements. This defaults to toggling, but other behaviors can be used as well.</p>\n', NULL, 'use Ajax\\semantic\\html\\collections\\form\\HtmlFormCheckbox;\nuse Ajax\\semantic\\html\\base\\constants\\CheckboxType;\n\n$btToggle=$semantic->htmlButton("bt-toggle","Toogle");\necho $btToggle->setToggle();\n\n$btCheck=$semantic->htmlButton("bt-check","Check");\necho $btCheck->setPositive();\n\n$btUncheck=$semantic->htmlButton("bt-uncheck","Uncheck");\necho $btUncheck->setNegative();\n\n$grid=$semantic->htmlGrid("GridCheckboxes");\n$grid->asSegment()->setDivided();\nfor($i=0;$i<6;$i++){\n    $ck=new HtmlFormCheckbox("ck".$i,"ck ".$i,NULL,CheckboxType::getConstantValues()[$i%3]);\n    $ck->attachEvents(["#bt-toggle","check"=>"#bt-check","uncheck"=>"#bt-uncheck"]);\n    $grid->addCol()->setValue($ck);\n}\necho $grid->compile($this->jquery);', 54, 1, 1, 0, ''),
(214, 'Fade', '<p>An element can disappear to reveal content below</p>\n', NULL, 'use Ajax\\semantic\\html\\elements\\HtmlImage;\n$rev=$semantic->htmlReveal("rev1",HtmlImage::small("","http://semantic-ui.com/images/wireframe/square-image.png"),HtmlImage::small("","http://semantic-ui.com/images/avatar/large/ade.jpg"));\necho $rev;', 58, 1, 1, 0, ''),
(215, 'Move', '<p>An element can move in a direction to reveal content</p>\n', NULL, 'use Ajax\\semantic\\html\\elements\\HtmlImage;\nuse Ajax\\semantic\\html\\base\\constants\\RevealType;\n$rev=$semantic->htmlReveal("rev2",HtmlImage::small("","http://semantic-ui.com/images/wireframe/square-image.png"),HtmlImage::small("","http://semantic-ui.com/images/avatar/large/chris.jpg"));\n$rev->setType(RevealType::MOVE);\necho $rev;', 58, 1, 2, 0, ''),
(216, 'Move in direction', NULL, NULL, 'use Ajax\\semantic\\html\\elements\\HtmlImage;\nuse Ajax\\semantic\\html\\base\\constants\\Direction;\n\n$rev=$semantic->htmlReveal("rev3",HtmlImage::small("","http://semantic-ui.com/images/wireframe/square-image.png"),HtmlImage::small("","http://semantic-ui.com/images/avatar/large/justen.jpg"));\n$rev->setMove(Direction::DOWN);\necho $rev;', 58, 1, 3, 0, ''),
(217, 'Rotate', '<p>An element can rotate to reveal content below</p>\n', NULL, 'use Ajax\\common\\html\\html5\\HtmlImg;\nuse Ajax\\semantic\\html\\base\\constants\\Size;\nuse Ajax\\semantic\\html\\base\\constants\\Direction;\n\n$rev=$semantic->htmlReveal("rev4",new HtmlImg("","http://semantic-ui.com/images/wireframe/square-image.png"),new HtmlImg("","http://semantic-ui.com/images/avatar/large/justen.jpg"));\n$rev->setRotate(Direction::DOWN);\necho $rev->setCircular()->setSize(Size::SMALL);', 58, 1, 4, 2, ''),
(218, 'Text', NULL, NULL, '$rev=$semantic->htmlReveal("rev5","Visible content","Hidden content");\necho $rev;', 59, 1, 1, 0, ''),
(219, 'Other elements', NULL, NULL, 'use Ajax\\semantic\\html\\elements\\HtmlLabel;\n$rev=$semantic->htmlReveal("rev5",new HtmlLabel("","Visible content","hide"),new HtmlLabel("","Hidden content","unhide"));\necho $rev->setMove();', 59, 1, 2, 0, ''),
(220, 'Step', '<p>A single step</p>\n', NULL, '$step=$semantic->htmlStep("steps1",["Shipping"]);\necho $step;', 60, 1, 1, 0, ''),
(221, 'Groups', '<p>A set of steps</p>\n', NULL, '$step=$semantic->htmlStep("steps2",[["truck","Shipping","Choose your shipping options"]]);\n$step->addStep(["payment","Billing","Enter billing information","active"]);\n$step->addStep(["info","Confirm Order","","disabled"]);\necho $step;\n', 60, 1, 2, 4, ''),
(222, 'Ordered steps', '<p>A step can show an ordered sequence of steps</p>\n', NULL, '$step=$semantic->htmlStep("steps3");\n$step->addStep(["title"=>"Shipping","description"=>"Choose your shipping options"]);\n$step->addStep(["title"=>"Billing","description"=>"Enter billing information"]);\n$step->addStep(["title"=>"Confirm Order","description"=>"Verify order details"]);\n$step->setActiveStep(1);\necho $step->setOrdered();', 60, 1, 3, 0, ''),
(223, 'Vertical', '<p>A step can be displayed stacked vertically</p>\n', NULL, '$step=$semantic->htmlStep("steps4");\n$step->addStep(["title"=>"Shipping","description"=>"Choose your shipping options"]);\n$step->addStep(["title"=>"Billing","description"=>"Enter billing information"]);\n$step->addStep(["title"=>"Confirm Order","description"=>"Verify order details"]);\n$step->setActiveStep(2);\necho $step->setOrdered()->setVertical();', 61, 1, 4, 0, ''),
(224, 'Attached', NULL, NULL, 'use Ajax\\semantic\\html\\base\\constants\\Side;\n$step=$semantic->htmlStep("steps5");\n$step->addStep(["title"=>"Shipping","description"=>"Choose your shipping options"]);\n$step->addStep(["title"=>"Billing","description"=>"Enter billing information"]);\n$step->addStep(["title"=>"Confirm Order","description"=>"Verify order details"]);\n$segment=$semantic->htmlSegment("seg","Steps can be attached to other elements")->setInverted();\necho $step->setAttached(Side::TOP,$segment);\necho $segment;\necho $step->setIdentifier("step6")->setAttached(Side::BOTTOM,$segment);', 61, 1, 5, 0, ''),
(225, 'Fluid', '<p>A fluid step takes up the width of its container</p>\n', NULL, '$step=$semantic->htmlStep("steps7");\n$step->addStep(["title"=>"Shipping","description"=>"Choose your shipping options"]);\n$step->addStep(["title"=>"Billing","description"=>"Enter billing information"]);\n$step->setOrdered()->setFluid()->setVertical();\n$grid=$semantic->htmlGrid("");\n$grid->addCol(12)->setValue($step);\necho $grid;', 61, 1, 6, 0, ''),
(226, 'Link', '<p>A step can link</p>\n', NULL, '$step=$semantic->htmlStep("steps8");\n$step->addStep(["title"=>"Shipping","description"=>"Choose your shipping options"]);\n$step->addStep(["title"=>"Billing","description"=>"Enter billing information"]);\n$step->addStep(["title"=>"Confirm Order","description"=>"Verify order details"]);\necho $step->setOrdered()->asLink();', 60, 1, 4, 0, ''),
(227, 'Table', '<p>A standard table</p>\n', NULL, 'use Ajax\\semantic\\html\\elements\\HtmlLabel;\nuse Ajax\\semantic\\html\\collections\\menus\\HtmlPaginationMenu;\n$table=$semantic->htmlTable("table1",3,3);\n$table->getHeader()->setValues("Header");\n$table->setValues("Cell")->setCelled();\n$table->getCell(0,0)->setValue(HtmlLabel::ribbon("","First"));\n$table->getFooter()->mergeCol();\n$menu=new HtmlPaginationMenu("",[1,2,3,4]);\n$menu->floatRight();\n$table->getFooter()->setValues($menu);\necho $table;', 62, 1, 1, 1, ''),
(228, 'Other table', NULL, NULL, 'use Ajax\\semantic\\html\\elements\\HtmlHeader;\nuse Ajax\\semantic\\html\\modules\\HtmlRating;\n$table=$semantic->htmlTable("table2",2,5);\n$table->getHeader()->setValues([["Evidence Rating","Effect","Efficacy","Consensus","Comments"]]);\n$table->setValues("Cell")->setCelled();\n$table->setColValues(0,new HtmlHeader("",2,"A"))->colCenter(0);\n$table->setColValues(1,["Power Output","Weight"]);\n$table->setColValues(2,[new HtmlRating("rate1",4,5,"user"),new HtmlRating("rate2",5,5)])->colCenter(2);\n$table->setColValues(3,["80% <br><a href=''#''>18 studies</a>","100% <br><a href=''#''>65 studies</a>"])->colRight(3);\n$table->setColValues(4,["Creatine supplementation is the reference compound for increasing muscular creatine levels; there is variability in this increase, however, with some nonresponders.","Creatine is the reference compound for power improvement, with numbers from one meta-analysis to assess potency"]);\necho $table->compile($this->jquery);', 62, 1, 2, 0, ''),
(229, 'Rating', '<p>A basic rating</p>\n\n<p>{{warning:<b>HtmlRating</b> element module must be compiled using the <b>$this-&gt;jquery</b> variable}}</p>\n', NULL, '$rate=$semantic->htmlRating("rate1",0,1);\necho $rate->compile($this->jquery);', 63, 1, 1, 0, ''),
(230, 'Star', '<p>A rating can use a set of star icons</p>\n', NULL, '$rate=$semantic->htmlRating("rate2",2,5,"star");\necho $rate->compile($this->jquery);', 63, 1, 2, 0, ''),
(231, 'Heart', '<p>A rating can use a set of heart icons</p>\n', NULL, '$rate=$semantic->htmlRating("rate3",4,5,"heart");\necho $rate->compile($this->jquery);', 63, 1, 3, 1, ''),
(232, 'Size', '<p>A rating can vary in size</p>\n', NULL, 'use Ajax\\semantic\\html\\base\\constants\\size;\n$i=0;\nforeach(Size::getConstants() as $size){\n	$rate=$semantic->htmlRating("rate-".$i++,3,5);\n	$rate->setSize($size);\n	echo $rate->compile($this->jquery);\n	echo "<br>";\n}', 64, 1, 1, 0, ''),
(233, 'Basic table', NULL, NULL, 'use Ajax\\semantic\\html\\elements\\HtmlHeader;\n$table=$semantic->htmlTable("table3",5,2);\n$table->setBasic(true)->setCollapsing()->setCelled();\n$table->getHeader()->setValues(["Employee","Correct Guesses"]);\n$table->setRowValues(0,[HtmlHeader::image("","http://semantic-ui.com/images/avatar2/small/lena.png",4,"Lena","Human Resources"),22]);\n$table->setRowValues(1,[HtmlHeader::image("","http://semantic-ui.com/images/avatar2/small/matthew.png",4,"Matthew","Fabric Design"),15]);\n$table->setRowValues(2,[HtmlHeader::image("","http://semantic-ui.com/images/avatar2/small/lindsay.png",4,"Lindsay","Entertainment"),12]);\n$table->setRowValues(3,[HtmlHeader::image("","http://semantic-ui.com/images/avatar2/small/mark.png",4,"Mark","Executive"),11]);\necho $table;', 62, 1, 3, 0, ''),
(234, 'Definition', '<p>A table may be formatted to emphasize a first column that defines a rows content</p>\n\n<p>{{info:Definition tables are designed to display on a single background color. You can adjust this by changing @definitionPageBackground, or specifying a background color on the first cell}}</p>\n', NULL, '$table=$semantic->htmlTable(''table4'',2,3);\n$table->setHeaderValues(["","Arguments","Description"]);\n$table->setValues([["reset rating","None","Resets rating to default value"],["set rating","rating (integer)","Sets the current star rating to specified value"]]);\n$table->setDefinition();\necho $table;', 62, 1, 4, 0, ''),
(235, 'Definition with elements', NULL, NULL, 'use Ajax\\semantic\\html\\modules\\checkbox\\HtmlCheckbox;\nuse Ajax\\semantic\\html\\elements\\HtmlButton;\n$table=$semantic->htmlTable(''table5'',3,5);\n$table->setHeaderValues(["","Name","Registration Date","E-mail address","Premium Plan"]);\n$table->setColValues(0,[HtmlCheckbox::slider("ck1")->setFitted(),HtmlCheckbox::slider("ck2"),HtmlCheckbox::slider("ck3")]);\n$table->setColValues(1,["John Lilki","Jamie Harington","Jill Lewis"]);\n$table->setColValues(2,["September 14, 2013","January 11, 2014","May 11, 2014"]);\n$table->setColValues(3,["jhlilk22@yahoo.com","jamieharingonton@yahoo.com","jilsewris22@yahoo.com"]);\n$table->setColValues(4,["No","Yes","Yes"]);\n$table->addColVariations(0,"collapsing");\n$footer=$table->getFooter()->setFullWidth();\n$footer->mergeCol(0,1);\n$footer->getCell(0,1)->setValue([new HtmlButton(''bt1'',''Approve''),new HtmlButton(''bt2'',''Approve all''),HtmlButton::labeled("btL","Add user","user")->setFloated("right")->setColor(''blue'')]);\n$table->addVariation("compact")->setDefinition()->setCelled();\necho $table;', 62, 1, 5, 0, ''),
(236, 'Structured', '<p>A table can be formatted to display complex structured data</p>\n', NULL, 'use Ajax\\semantic\\html\\elements\\HtmlIcon;\n$table=$semantic->htmlTable(''table6'',4,6);\n$head=$table->getHeader();\n$head->addRow(6);\n$head->getCell(0,0)->setRowspan(2);\n$head->getCell(0,1)->setRowspan(2);\n$head->getCell(0,2)->setRowspan(2);\n$head->setValues(["Names","Types","Files","Languages"]);\n$head->getCell(0,3)->setColspan(3);\n$head->setRowValues(1,["Ruby","Javascript","Python"]);\n$table->getCell(1,0)->setRowspan(3);\n$check=new HtmlIcon("","large green checkmark");\n$table->setValues([["Alpha Team","Project 1",2,$check],["Beta Team","Project 1",52,$check],["Project 2",12,NULL,$check],["Project 3",21,$check]]);\n$table->colCenter([3,4,5]);\necho $table->setCelled()->setStructured();\n', 62, 1, 6, 0, ''),
(237, 'Positive/Negative', '<p>A cell or row may let a user know whether a value is good or bad</p>\n', NULL, '$table=$semantic->htmlTable("table7",4,3);\n$table->setHeaderValues(["Name","Status","Notes"]);\n$table->setValues([["No Name Specified","Unknown","None"],["Jimmy","Approved","None"],["Jamie","Unknown","Requires call"],["Jill","Unknown","None"]]);\n$table->getRow(1)->setPositive();\n$table->getCell(0,2)->setNegative();\necho $table;', 65, 1, 1, 0, ''),
(238, 'Error', '<p>A cell or row may call attention to an error or a negative value</p>\n', NULL, '$table=$semantic->htmlTable("table8",4,3);\n$table->setHeaderValues(["Name","Status","Notes"]);\n$table->setValues([["No Name Specified","Unknown","None"],["Jimmy","Approved","None"],["Jamie","Unknown","Requires call"],["Jill","Unknown","None"]]);\n$table->getRow(1)->setError();\n$table->getCell(0,2)->setError();\necho $table;', 65, 1, 8, 0, ''),
(239, 'Warning', '<p>A cell or row may warn a user</p>\n', NULL, '$table=$semantic->htmlTable("table9",4,3);\n$table->setHeaderValues(["Name","Status","Notes"]); $table->setValues([["No Name Specified","Unknown","None"],["Jimmy","Approved","None"],["Jamie","Unknown","Requires call"],["Jill","Unknown","None"]]);\n$table->getRow(1)->setWarning();\n$table->getCell(0,2)->setWarning();\necho $table;', 65, 1, 9, 0, ''),
(240, 'Single line', '<p>A table can specify that its cell contents should remain on a single line, and not wrap.</p>\n', NULL, '$table=$semantic->htmlTable("table10",3,4);\n$table->setHeaderValues(["Name","Registration Date","E-mail address","Premium Plan"]);\n$table->setColValues(0,["John Lilki","Jamie Harington","Jill Lewis"]);\n$table->setColValues(1,["September 14, 2013","January 11, 2014","May 11, 2014"]);\n$table->setColValues(2,["jhlilk22@yahoo.com","jamieharingonton@yahoo.com","jilsewris22@yahoo.com"]);\n$table->setColValues(3,["No","Yes","Yes"]);\necho $table->setSingleLine();', 66, 1, 1, 0, ''),
(241, 'Fixed', '<p><span style="color: rgba(0, 0, 0, 0.8);">A table can use&nbsp;</span><a href="https://developer.mozilla.org/en-US/docs/Web/CSS/table-layout" style="box-sizing: inherit; color: rgb(65, 131, 196); text-decoration: none; display: inline-block; position: relative; overflow: hidden; vertical-align: top; z-index: 0; transition: color 0.2s; outline: none; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: 0px 0px; background-repeat: initial;"><code style="box-sizing: inherit; border-radius: 3px; display: inline-block; font-weight: bold; padding: 1px 6px; vertical-align: baseline; background-color: rgba(0, 50, 100, 0.0784314);">table-layout: fixed</code></a><span style="color: rgba(0, 0, 0, 0.8); ">&nbsp;a special faster form of table rendering that does not resize table cells based on content.</span></p>\n', NULL, '$table=$semantic->htmlTable("table11",3,3);\n$table->setHeaderValues(["Name","Registration","Date","E-mail address","Premium Plan"]);\n$table->setRowValues(0,["John","Approved","John is an interesting boy but sometimes you don''t really have enough room to describe everything you''d like"]);\n$table->setRowValues(1,["Jamie","Approved","Jamie is a kind girl but sometimes you don''t really have enough room to describe everything you''d like"]);\n$table->setRowValues(2,["Jill","Denied","Jill is an alright girl but sometimes you don''t really have enough room to describe everything you''d like"]);\necho $table->setFixed();', 66, 1, 2, 0, ''),
(242, 'Sortable', '<p>{{warning:<span style="color: rgb(87, 58, 8); background-color: rgb(255, 250, 243);">Adding a classname of&nbsp;</span><code style="box-sizing: inherit; border-radius: 3px; display: inline-block; font-weight: bold; padding: 3px 4px; vertical-align: baseline; line-height: 1; margin-top: 0px; color: rgb(87, 58, 8); background-color: rgba(0, 0, 0, 0.0588235);">ascending</code><span style="color: rgb(87, 58, 8); background-color: rgb(255, 250, 243);">&nbsp;or&nbsp;</span><code style="box-sizing: inherit;border-radius: 3px; display: inline-block; font-weight: bold; padding: 3px 4px; vertical-align: baseline; line-height: 1; color: rgb(87, 58, 8); background-color: rgba(0, 0, 0, 0.0588235);">descending</code><span style="color: rgb(87, 58, 8);background-color: rgb(255, 250, 243);">&nbsp;to the&nbsp;</span><code style="box-sizing: inherit;border-radius: 3px; display: inline-block; font-weight: bold; padding: 3px 4px; vertical-align: baseline; line-height: 1; color: rgb(87, 58, 8); background-color: rgba(0, 0, 0, 0.0588235);">th</code><span style="color: rgb(87, 58, 8);background-color: rgb(255, 250, 243);">&nbsp;will show the user the direction of sort. This example uses a modified version of the kylefox''s&nbsp;</span><a href="https://github.com/kylefox/jquery-tablesort" style="box-sizing: inherit; color: rgb(65, 131, 196); text-decoration: none; margin-bottom: 0px;background: 0px 0px;">tablesort plugin</a><span style="color: rgb(87, 58, 8);background-color: rgb(255, 250, 243);">&nbsp;to provide the proper class names.</span>}}</p>\n', NULL, '$table=$semantic->htmlTable("s-table",3,3);\n$table->setHeaderValues(["Name","Status","Notes"]);\n$table->setRowValues(0,["John","No Action","None"]);\n$table->setRowValues(1,["Jill","Denied","None"]);\n$table->setRowValues(2,["Jamie","Approved","Requires call"]);\n$table->setFooterValues(["3 People","2 Approved"]);\n$table->conditionalCellFormat(function($cell){return $cell->getContent()=="Denied";},"negative");\n$table->conditionalRowFormat(function($row){return $row->containsStr("Approved");},"positive");\n$table->setSortable(0);\necho $table->compile($this->jquery);\n', 66, 1, 30, 0, ''),
(243, 'Selectable rows', '<p>A table can have its rows appear selectable</p>\n', NULL, '$table=$semantic->htmlTable("table-selectable",3,4);\n$table->setHeaderValues(["Name","Registration Date","E-mail address","Premium Plan"]);\n$table->setColValues(0,["John Lilki","Jamie Harington","Jill Lewis"]);\n$table->setColValues(1,["September 14, 2013","January 11, 2014","May 11, 2014"]);\n$table->setColValues(2,["jhlilk22@yahoo.com","jamieharingonton@yahoo.com","jilsewris22@yahoo.com"]);\n$table->setColValues(3,["No","Yes","Yes"]);\necho $table->setSelectable();', 66, 1, 3, 0, ''),
(244, 'Selectable cells', '<p>A table cell can be selectable {{info:Using an a link inside a selectable cell will automatically make the hit box the entire cell area. By default links will inherit their cell color.}}</p>\n', NULL, '$table=$semantic->htmlTable("s-cell-table",6,3);\n$table->setHeaderValues(["Name","Status","Edit"]);\n$table->setValues([["John","No Action","Edit"],["Jamie","Approved","Edit"],["Jill","Denied","Edit"],["John","No Action","Requires change"],\n["Jamie","Approved","Approve"],["Jill","Denied","Remove"]]);\n$table->applyCells(function($cell){if($cell->getContent()=="Edit") $cell->setSelectable();});\necho $table->setCelled();\n', 66, 1, 5, 0, ''),
(245, 'From model', NULL, NULL, '$table=$semantic->htmlTable("clients",0,4);\n$clients=Client::find();\n$table->setHeaderValues(["id","Name","Part","Email"]);\n$table->fromDatabaseObjects($clients, function($client){\n		return $client->toArray();\n	});\necho $table;', 66, 1, 6, 0, ''),
(246, 'From model with more details', NULL, NULL, '$table=$semantic->htmlTable("clients-2",0,4);\n$clients=Client::find();\n$table->setHeaderValues(["id","Name","Part","Email"]);\n$table->fromDatabaseObjects($clients, function($client) use ($table){\n		$result=$table->newRow();\n		$result->setValues($client->toArray());\n		if($client->getPart()<30) $result->setPositive();\n		return $result;\n	});\necho $table;', 66, 1, 7, 0, ''),
(247, 'Standard', '<p>A standard progress bar</p>\n', NULL, 'use Ajax\\semantic\\html\\base\\constants\\Color;\n$pb=$semantic->htmlProgress("pb1",40,"Uploading files");\necho $pb->compile($this->jquery);\n\n$bts=$semantic->htmlButtonGroups("bts1",["minus","plus"],true);\n$bts->addClasses(["decrement red","increment green"]);\necho $bts->compile();', 68, 1, 1, 1, ''),
(248, 'Indicating', '<p>An indicating progress bar visually indicates the current level of progress of a task</p>\n', NULL, 'use Ajax\\semantic\\html\\base\\constants\\Color;\n$pb=$semantic->htmlProgress("pb2",10,"Funding");\n$pb->setIndicating();\n$pb->setTextValues(["active"=>"{percent}% Funded","success"=>"Project funded !"]);\necho $pb->compile($this->jquery);\n\n$bts=$semantic->htmlButtonGroups("bts2",["minus","plus"],true);\n$bts->addClasses(["decrement red","increment green"]);\necho $bts;', 68, 1, 2, 0, ''),
(250, 'Adding a Total Value', '<p>A progress bar can keep track of the current value as a ratio of a total value. This is useful for tracking the progress of a series of events with a known quantity, for example "uploading 1 of 20" photos.</p>\n', NULL, 'use Ajax\\semantic\\html\\base\\constants\\Color;\n$pb=$semantic->htmlProgress("pb3",1,"Adding Photos");\n$pb->setTotal(20);\n$pb->setTextValues(["active"=>"Adding {value} of {total} photos","success"=>"{total} Photos uploaded !"]);\necho $pb->compile($this->jquery);\n\n$bts=$semantic->htmlButtonGroups("bts3",["minus","plus"],true);\n$bts->addClasses(["decrement red","increment green"]);\necho $bts;', 68, 1, 3, 0, ''),
(251, 'Active', '<p>A progress bar can show activity</p>\n', NULL, '$pb=$semantic->htmlProgress("pb-active",50,"Uploading Files");\necho $pb->setActive();', 69, 1, 1, 0, ''),
(252, 'Success', '<p>A progress bar can show a success state</p>\n', NULL, '$pb=$semantic->htmlProgress("pb-success",100,"Everything worked, your file is all ready.");\necho $pb;', 69, 1, 2, 0, ''),
(253, 'Warning', '<p>A progress bar can show a warning state</p>\n', NULL, '$pb=$semantic->htmlProgress("pb-warning",100,"Your file didn''t meet the minimum resolution requirements.");\necho $pb->setWarning();\n', 69, 1, 3, 0, ''),
(254, 'Error', '<p>A progress bar can show an error state</p>\n', NULL, '$pb=$semantic->htmlProgress("pb-error",100,"There was an error.");\necho $pb->setError();', 69, 1, 4, 0, ''),
(255, 'Disabled', '<p>A progress bar can be disabled</p>\n', NULL, '$pb=$semantic->htmlProgress("pb-disabled",30);\necho $pb->setDisabled();', 69, 1, 4, 0, ''),
(256, 'Inverted', '<p>A progress bar can have its colors inverted</p>\n', NULL, 'use Ajax\\semantic\\html\\modules\\HtmlProgress;\n$seg=$semantic->htmlSegment("inverted-seg")->setInverted();\n$seg->setContent([HtmlProgress::create("pbA",37,"Uploading files",["inverted"]),HtmlProgress::create("pbB",100,"Success",["inverted"]),HtmlProgress::create("pbC",100,"Warning",["inverted","warning"])]);\necho $seg->compile($this->jquery);\n\n$bts=$semantic->htmlButtonGroups("bts4",["plus"],true);\n$bts->addClasses(["increment green"]);\necho $bts;', 70, 1, 1, 0, ''),
(257, 'Colors', '<p>A progress bar can have different colors</p>\n', NULL, 'use Ajax\\semantic\\html\\base\\constants\\Color;\nforeach(Color::getConstants() as $k=>$v){\necho $semantic->htmlProgress("c-".$k,\\rand(0,99),$k)->setColor($v)->compile($this->jquery);\n}\n\n$bts=$semantic->htmlButtonGroups("bts5",["minus","plus"],true);\n$bts->addClasses(["decrement red","increment green"]);\necho $bts;', 70, 1, 5, 0, ''),
(258, 'Sizes', NULL, NULL, 'use Ajax\\semantic\\html\\base\\constants\\Size;\nforeach(Size::getConstants() as $k=>$v){\necho $semantic->htmlProgress("s-".$k,\\rand(0,99),$k)->setSize($v)->compile($this->jquery);\n}\n\n$bts=$semantic->htmlButtonGroups("bts6",["minus","plus"],true);\n$bts->addClasses(["decrement red","increment green"]);\necho $bts;', 70, 1, 6, 0, ''),
(259, 'Local search', NULL, NULL, '$search=$semantic->htmlSearch("search1","Search countries...","search");\n$search->addResults([["title"=>"France"],["title"=>"Germany"],["title"=>"Great Britain"]]);\necho $search->compile($this->jquery);', 71, 1, 1, 1, ''),
(260, 'Category remote search', '<p>A search can display results from remote content ordered by categories</p>\n\n<pre class="language-php">\n<code class="language-php">\n	public function categorySearchAction($query=NULL) {\n		$this-&gt;view-&gt;disable();\n		$this-&gt;response-&gt;setContentType(''application/json'', ''UTF-8'');\n		$search=new SearchCategories();\n		$search-&gt;add([ [ "title" =&gt; "France" ],[ "title" =&gt; "Germany" ],[ "title" =&gt; "Great Britain" ] ], "Europe");\n		$search-&gt;add([ "Afghanistan","Armenia","Azerbaijan" ], "Asia");\n		if (isset($query))\n			echo $search-&gt;search($query)-&gt;getResponse();\n		else\n			echo $search-&gt;getResponse();\n	}\n</code>\n</pre>\n', NULL, '$search=$semantic->htmlSearch("search3","Search countries...","search");\n$search->setUrl($this->url->get("/json/categorySearch/{query}"))->setType("category");\necho $search->compile($this->jquery);', 71, 1, 3, 0, ''),
(261, 'Standard remote search from database', '<pre class="language-php">\n<code class="language-php">\n	public function standardSearchDbAction($query=NULL) {\n		$this-&gt;view-&gt;disable();\n		$this-&gt;response-&gt;setContentType(''application/json'', ''UTF-8'');\n		$search=new SearchResults();\n		if (isset($query) === false) {\n			$countries=Countries::find();\n		} else {\n			$countries=Countries::find("countryName like ''%" . $query . "%''");\n		}\n		$search-&gt;fromDatabaseObjects($countries, function ($country) {\n			return $country-&gt;getCountryName();\n		});\n		echo $search-&gt;getResponse();\n	}\n</code>\n</pre>\n', NULL, '$search=$semantic->htmlSearch("search4","Search countries...","search");\n$search->setUrl($this->url->get("/json/standardSearchDb/{query}"))->setType("standard");\necho $search->compile($this->jquery);', 71, 1, 4, 0, ''),
(262, 'Category remote search from database', '<pre class="language-php">\n<code class="language-php">\n	public function categorySearchDbAction($query=NULL) {\n		$this-&gt;view-&gt;disable();\n		$this-&gt;response-&gt;setContentType(''application/json'', ''UTF-8'');\n		$search=new SearchCategories();\n		if (isset($query) === false) {\n			$countries=Countries::find();\n		} else {\n			$countries=Countries::find("countryName like ''%" . $query . "%''");\n		}\n		$search-&gt;fromDatabaseObjects($countries, function ($country) use($search) {\n			$search-&gt;add($country-&gt;getCountryName(), $country-&gt;getContinentName());\n		});\n		echo $search-&gt;getResponse();\n	}\n</code>\n</pre>\n', NULL, '$search=$semantic->htmlSearch("search5","Search countries...","search");\n$search->setUrl($this->url->get("/json/categorySearchDb/{query}"))->setType("category")->setFluid();\necho $search->compile($this->jquery);', 71, 1, 5, 0, ''),
(263, 'Standard remote search', '<p>A search can display a set of results, created in a phalcon controller :</p>\n\n<pre class="language-php">\n<code>\n	public function standardSearchAction($query=NULL) {\n		$this-&gt;view-&gt;disable();\n		$this-&gt;response-&gt;setContentType(''application/json'', ''UTF-8'');\n		$search=new SearchResults();\n		$search-&gt;addResults([ "France","Germany","Great Britain" ]);\n		if (isset($query)) {\n			echo $search-&gt;search($query)-&gt;getResponse();\n		} else\n			echo $search-&gt;getResponse();\n	}\n</code>\n</pre>\n', NULL, '$search=$semantic->htmlSearch("search2","Search countries...","search");$search->setUrl($this->url->get("/json/standardSearch/{query}"));echo $search->compile($this->jquery);', 71, 1, 2, 0, ''),
(264, 'Element attributes', '<pre class="language-php">\n<code class="language-php">\n	public function imageSearchAction($query=NULL) {\n		$this-&gt;view-&gt;disable();\n		$this-&gt;response-&gt;setContentType(''application/json'', ''UTF-8'');\n		$search=new SearchCategories();\n		if (isset($query) === false) {\n			$countries=Countries::find();\n		} else {\n			$countries=Countries::find("countryName like ''%" . $query . "%''");\n		}\n		$search-&gt;fromDatabaseObjects($countries, function ($country) use($search) {\n			$code=$country-&gt;getCountryCode();\n			$description="Population : " . number_format($country-&gt;getPopulation()) . "\nCapital : " . $country-&gt;getCapital();\n			$image="https://lipis.github.io/flag-icon-css/flags/4x3/" . strtolower($code) . ".svg";\n			$search-&gt;add(new SearchResult($code, $country-&gt;getCountryName(), $description, $image), $country-&gt;getContinentName());\n		});\n		echo $search-&gt;getResponse();\n	}\n</code>\n</pre>\n', NULL, '$search=$semantic->htmlSearch("search6","Search countries...","search");\n$search->setUrl($this->url->get("/json/imageSearch/{query}"))->setType("category")->setFluid()->onSelect("");\necho $search->compile($this->jquery);', 71, 1, 6, 0, ''),
(265, 'Ajax request on select', '<p>.</p>\n', NULL, '$search=$semantic->htmlSearch("search7","Search countries...","search");\n$search->setUrl($this->url->get("/json/imageSearch/{query}"))->setType("category")->setFluid();\n$search->postOnSelect("Test/countryInfo","#reponse7");\necho $search->compile($this->jquery);\necho $semantic->htmlSegment("reponse7");', 71, 1, 7, 0, ''),
(266, 'Flag', NULL, NULL, 'echo $semantic->htmlFlag("","gb");\necho Ajax\\semantic\\html\\elements\\htmlFlag::France();\necho $semantic->htmlFlag("",Ajax\\semantic\\html\\base\\constants\\Country::UNITED_STATES);\necho Ajax\\semantic\\html\\elements\\htmlFlag::byNum(10);', 72, 1, 1, 1, ''),
(267, 'Types', '<p>{{info:Many flag sets use SVG to render flags at multiple sizes. However svg flags usually appear blurry or render improperly at smaller sizes. Semantic''s default theme uses an image sprite solution which provides crisper icons, but without the ability to resize}}</p>\n', NULL, 'use Ajax\\semantic\\html\\base\\constants\\Country;\nuse Ajax\\semantic\\html\\elements\\HtmlFlag;\n$table=$semantic->htmlTable("flags-table",0,3);\n$table->setHeaderValues(["flag","Name","ISO 3166-2 Code"]);\nforeach(Country::getConstants() as $k=>$v){\n$table->addRow([new HtmlFlag("",$v),$k,$v]);\n}\n$table->colCenter(0);\n$table->setSortable()->setDefinition()->setCelled();\necho $table->compile($this->jquery);', 72, 1, 2, 0, ''),
(268, 'Simple dimmer', '<p>A simple dimmer displays no content</p>\n', NULL, '$seg=$semantic->htmlSegment("seg1","Segment content....");\necho $seg;\n\n$bts=$semantic->htmlButtonGroups("bts1",["hide","unhide"],true);\n$bts->addClasses(["hide red","show green"]);\necho $bts->compile();', 73, 1, 1, 0, ''),
(269, 'Content dimmer', '<p>A dimmer can display content</p>\n', NULL, 'use Ajax\\semantic\\html\\elements\\HtmlImage;\n$seg=$semantic->htmlSegment("seg2",[new HtmlImage('''',''http://semantic-ui.com/images/wireframe/image.png'','''',''small'')]);\n$seg->addDimmer()->asIcon("heart","Dimmed Message!");\necho $seg;\n\n$bts=$semantic->htmlButtonGroups("bts2",["hide","unhide"],true);\n$bts->addClasses(["hide red","show green"]);\necho $bts->compile();', 73, 1, 2, 0, ''),
(270, 'Page dimmer', NULL, NULL, '$dimmer=$semantic->htmlDimmer("pDimmer");\n$dimmer->asIcon("heart","Dimmed Message!","Dimmer subheader")->asPage();\necho $dimmer;\n\n$bts=$semantic->htmlButtonGroups("bts2",["unhide"],true);\n$bts->addClasses(["page green"]);\necho $bts->compile();', 73, 1, 3, 0, ''),
(271, 'Show event', NULL, NULL, 'use Ajax\\semantic\\html\\elements\\HtmlImage;\n$seg=$semantic->htmlSegment("seg3",[new HtmlImage('''',''http://semantic-ui.com/images/wireframe/image.png'','''',''small'')]);\n$seg->addDimmer(["on"=>"hover"])->asIcon("heart","Dimmed Message!","on mouseover");\necho $seg->compile($this->jquery);', 74, 1, 1, 0, ''),
(273, 'Card', '<p>A single card</p>\n', NULL, '$card=$semantic->htmlCard("card1");\n$card->addImage("http://semantic-ui.com/images/avatar2/large/kristy.png");\n$card->addCardHeaderContent("Kristy","Joined in 2013","Kristy is an art director living in New York.");\n$card->addExtraContent("22 Friends")->addIcon("user");\necho $card;', 75, 1, 1, 1, ''),
(274, 'Example', NULL, NULL, 'use Ajax\\semantic\\html\\elements\\HtmlInput;\nuse Ajax\\semantic\\html\\elements\\HtmlImage;\n$card=$semantic->htmlCard("card2");\n$content1=$card->addCardContent();\n$content1->addMeta("14h","right");\n$content1->addContent(HtmlImage::avatar("","http://semantic-ui.com/images/avatar/large/elliot.jpg","Elliot"));\n$card->addImage("http://semantic-ui.com/images/wireframe/image.png");\n$content2=$card->addCardContent();\n$content2->addContentIcon("heart outline like","17 likes","right");\n$content2->addContentIcon("comment","3 comments");\n$card->addExtraContent(HtmlInput::outline("text","heart","","Add comment..."));\necho $card;', 75, 1, 2, 0, ''),
(275, 'Reveal', NULL, NULL, 'use Ajax\\semantic\\html\\elements\\HtmlIcon;\n$card=$semantic->htmlCard("card3");\n$card->addRevealImage("http://semantic-ui.com/images/avatar/large/jenny.jpg","http://semantic-ui.com/images/avatar/large/elliot.jpg","slide");\n$card->addCardHeaderContent("Team Fu & Hess","Create in Sep 2014");\n$card->addExtraContent(HtmlIcon::label("","users","2 members")->asLink("#"));\necho $card;', 75, 1, 3, 0, ''),
(276, 'Dimmer with button', NULL, NULL, 'use Ajax\\semantic\\html\\elements\\HtmlButton;\n\n$img=$semantic->htmlImage("img",''http://semantic-ui.com/images/avatar2/large/kristy.png'','''',''medium'');\n$img->addDimmer(["on"=>"hover","opacity"=>0.5],HtmlButton::labeled("bt4","add friend","plus"))->setBlurring();\necho $img;', 74, 1, 4, 0, '');
INSERT INTO `exemple` (`id`, `titre`, `description`, `header`, `php`, `idRubrique`, `execPHP`, `ordre`, `demo`, `css`) VALUES
(277, 'Dimmer', NULL, NULL, 'use Ajax\\semantic\\html\\elements\\HtmlButton;\n\n$card=$semantic->htmlCard("card4");\n$img=$card->addImage("http://semantic-ui.com/images/avatar2/large/kristy.png");\n$img->addDimmer(["on"=>"hover","opacity"=>0.5],HtmlButton::labeled("bt4","add friend","plus"))->setBlurring();\n$card->addCardHeaderContent("Kristy","Joined in 2013","Kristy is an art director living in New York.");\n$card->addExtraContent("22 Friends")->addIcon("user");\necho $card;', 75, 1, 4, 0, ''),
(278, 'Header', NULL, NULL, '$card=$semantic->htmlCard("card1");\n$card->addCardHeaderContent("Elliot Fu","Friend","Elliot Fu is a film-maker from New York.");\necho $card;', 75, 1, 5, 0, ''),
(279, 'Metadata', '<p>A card can contain content metadata {{info:You can include an arbitrary amount of metadata using your own class conventions, all child elements will automatically be spaced}}</p>\n', NULL, 'use Ajax\\semantic\\html\\elements\\html5\\HtmlLink;\n$card=$semantic->htmlCard("card6");\n$card->addCardHeaderContent("Cute Dog",["2 days ago",new HtmlLink("","#","Animals")]);\necho $card;', 75, 1, 6, 0, ''),
(280, 'Approval', '<p>A card can contain a like or star action.</p>\n', NULL, 'use Ajax\\semantic\\html\\elements\\HtmlIcon;\n$card=$semantic->htmlCard("card7");\n$content=$card->addContent();\n$content->addContentIcons(["star","like"],"right");\n$content->addHeaderContent("Cute dog","<p>Nothing</p>");\n$extra=$card->addExtraContent();\n$extra->addContentIcon("star","Favorite","right");\n$extra->addContentIcon("like","Like");\necho $card;', 75, 1, 7, 0, ''),
(281, 'Link Card', '<p>A card can be formatted so that the entire contents link to another page</p>\n', NULL, 'use Ajax\\semantic\\html\\elements\\HtmlImage;\n$card=$semantic->htmlCard("card8");\n$card->addCardHeaderContent("Cute dog","Animals",new HtmlImage("","http://semantic-ui.com/images/wireframe/paragraph.png"));\n$card->addExtraContent(HtmlImage::avatar("","http://semantic-ui.com/images/avatar/small/matt.jpg","Matt"));\n$card->asLink("http://www.dog.com/");\necho $card;', 77, 1, 1, 0, ''),
(282, 'Creation', NULL, NULL, '$cards=$semantic->htmlCardGroups("cards1",[\n["Elliot Fu","Friends of Veronika","Elliot requested permission to view your contact details"],\n["Jenny Hess","New Member","Jenny wants to add you to the group best friends"]]);\n$cards->getCardContent(0,0)->addImage("http://semantic-ui.com/images/avatar/large/elliot.jpg","","mini")->setFloated("right");\n$bts=$semantic->htmlButtonGroups("bts1",["Approve","Decline"]);\n$bts->addClasses(["basic green","basic red"]);\n\n$cards->getCard(0)->addExtraContent($bts);\necho $cards;', 78, 1, 1, 1, ''),
(283, 'Loading from model with new Card', NULL, NULL, '$clients=Client::find();\n$cards=$semantic->htmlCardGroups("cards4");\n$cards->setWide(3);\n$cards->fromDatabaseObjects($clients,function($client) use ($cards){\n	$card=$cards->newCard("card-".$client->getId());\n	$card->addImage($client->getImage());\n	$card->addCardHeaderContent($client->getName(),$client->getMeta(),$client->getDescription());\n	$extra=$card->addExtraContent();\n	$extra->addContentIcon("user",$client->getFriends()." Friends");\n	$extra->addContentText("Joined in ".$client->getJoined(),"right");\n	return $card;\n});\necho $cards;', 78, 1, 4, 0, ''),
