(353, 'List', '<p>A list groups related content</p>\n', NULL, 'echo $semantic->htmlList("",["Apples","Pears","Oranges"]);\necho $semantic->htmlList("",[["users","Semantic UI"],["marker","New York, NY"],["mail","jack@semantic-ui.com"],["linkify","semantic-ui.com"]]);\n$ss=$semantic->htmlList("",[["icon"=>"folder","header"=>"src","description"=>"Source files for project"]]);\n$ss->getItem(0)->addList([["icon"=>"folder","header"=>"site","description"=>"Your site''s theme"],["icon"=>"folder","header"=>"themes","description"=>"Packaged theme files"]]);\necho $ss;', 93, 1, 1, 0, NULL),
(354, 'Bulleted', '<p>A list can mark items with a bullet</p>\n', NULL, '$list=$semantic->htmlList("",["Gaining Access","Inviting Friends",["title"=>"Benefits","items"=>["Use Anywhere","Rebates","Discounts"]],"Warranty"]);\necho $list->setBulleted();', 93, 1, 2, 0, NULL),
(355, 'Ordered', '<p>A list can be ordered numerically</p>\n', NULL, '$list=$semantic->htmlList("",["Signing Up","User Benefits",["title"=>"User types","items"=>["Admin","Power user","Regular user"]],"Deleting Your Account"]);\necho $list->setOrdered();', 93, 1, 3, 0, NULL),
(356, 'Link', '<p>A list can be specially formatted for navigation links</p>\n', NULL, '$list=$semantic->htmlList("",["Home","About","Jobs","Team"]);\n$list->asLink()->getItem(0)->setActive();\necho $list;', 93, 1, 4, 0, NULL),
